VALUES	(
			#{premiumGasoline}
		  , #{gasoline}
		  , #{diesel}
		  , #{lampOil}
		  , #{butane}
		  , CONVERT(CHAR(10), GETDATE(), 23)
		  , #{sido}	
		  , #{sigun}
	  	)

