mapper.update:
UPDATE dbo.T_PRIVATE_COL_DETAIL SET COL_IDX = COL_IDX_OUT , FIX_YN = FIX_YN_OUT , SHOW_YN = SHOW_YN_OUT , COL_WIDTH = COL_WIDTH_OUT , TEXT_ALIGN = TEXT_ALIGN_OUT , FONT_SIZE = FONT_SIZE_OUT , COLOR = COLOR_OUT , FONT_WEIGHT = FONT_WEIGHT_OUT , FONT_STYLE = FONT_STYLE_OUT , EDITID = #{userId} , EDITDATE = getdate() FROM (
SELECT #{item.userId, jdbcType=VARCHAR} USER_ID_OUT , #{item.menuCode, jdbcType=VARCHAR} MENU_CODE_OUT , #{item.gridId, jdbcType=VARCHAR} GRID_ID_OUT , #{item.colId, jdbcType=VARCHAR} COL_ID_OUT , #{item.colName, jdbcType=VARCHAR} COL_NAME_OUT , #{item.colIdx, jdbcType=INTEGER} COL_IDX_OUT , #{item.fixYn, jdbcType=VARCHAR} FIX_YN_OUT , #{item.showYn, jdbcType=VARCHAR} SHOW_YN_OUT , #{item.colWidth, jdbcType=INTEGER} COL_WIDTH_OUT , #{item.textAlign, jdbcType=INTEGER} TEXT_ALIGN_OUT , #{item.fontSize, jdbcType=INTEGER} FONT_SIZE_OUT , #{item.color, jdbcType=VARCHAR} COLOR_OUT , #{item.fontWeight, jdbcType=VARCHAR} FONT_WEIGHT_OUT , #{item.fontStyle, jdbcType=VARCHAR} FONT_STYLE_OUT
) SUB WHERE dbo.T_PRIVATE_COL_DETAIL.USER_ID = SUB.USER_ID_OUT AND dbo.T_PRIVATE_COL_DETAIL.MENU_CODE = SUB.MENU_CODE_OUT AND dbo.T_PRIVATE_COL_DETAIL.GRID_ID = SUB.GRID_ID_OUT AND dbo.T_PRIVATE_COL_DETAIL.COL_ID = SUB.COL_ID_OUT

mapper.select:
SELECT  MENU_CODE
      , GRID_ID
      , PAGING_CNT
  FROM dbo.T_PRIVATE_TBL_DETAIL
 WHERE 1=1
   AND USER_ID = #{userId}
   AND MENU_CODE = #{pageId}
   AND GRID_ID = #{gridId}

mapper.delete:
DELETE
  FROM dbo.T_PRIVATE_COL_DETAIL 
 WHERE 1=1
   AND USER_ID = #{userId}
   AND MENU_CODE = #{pageId}
   AND GRID_ID = #{gridId}

mapper.update:
UPDATE dbo.T_PRIVATE_TBL_DETAIL
   SET PAGING_CNT = #{pagingCnt}
 WHERE 1=1
   AND USER_ID = #{userId}
   AND MENU_CODE = #{pageId}
   AND GRID_ID = #{gridId}
