mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY deptName ASC) AS number , A.* FROM ( SELECT CUST_ID custId , DEPT_ID deptId , DEPT_NAME deptName , USE_YN useYn , REGDATE regdate FROM T_CUST_DEPT WHERE CUST_ID = #{custId}
AND USE_YN = 'Y'
AND DEPT_ID = #{deptId}
) A ORDER BY number ASC

mapper.insert:
{call INSERT_COMP(
				   	#{custId}
				  , #{bizName}
				  , #{custTypeCode}
				  , #{bizNum}
				  , #{bizNumSub}
				  , #{ceo}
				  , #{bizCond}
				  , #{bizKind}
				  , #{bizAddr}
				  , #{regId}
				  , #{bizTypeCode}
				  , #{bizPost}
				  , #{bizAddrDetail}
				  , #{editId}
				  , #{bankCode} 
				  , #{bankCnnm} 
				  , #{bankAccount}
				  , #{dlineDayCode}
				  , #{dlinePointCode}
				  , #{retCode, mode=OUT, jdbcType=NVARCHAR, javaType=String}
				  ,	#{retMsg, mode=OUT, jdbcType=NVARCHAR, javaType=String}
				  , #{custId, mode=OUT, jdbcType=NVARCHAR, javaType=String})
}

{call INSERT_DEPT(
				   	#{custId}
				  , #{deptId}
				  , #{deptName}
				  , #{regId}
				  , #{retCode, mode=OUT, jdbcType=NVARCHAR, javaType=String}
				  ,	#{retMsg, mode=OUT, jdbcType=NVARCHAR, javaType=String}
				  , #{custId, mode=OUT, jdbcType=NVARCHAR, javaType=String}
				  , #{deptId, mode=OUT, jdbcType=NVARCHAR, javaType=String})
}

mapper.update:
UPDATE T_CUST_DEPT 
SET USE_YN = #{useYn}
WHERE CUST_ID = #{custId}
AND DEPT_ID = #{deptId}

UPDATE T_CUST_MST
SET	OAPI_TOKEN = #{token},
	OAPI_YN = #{oapiYn}
WHERE CUST_ID = #{custId}
