SELECT AA.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY AAA.regdate desc) number , AAA.* FROM ( SELECT A.ORDER_ID orderId , B.REQ_CUST_ID reqCustId , B.REQ_DEPT_ID reqDeptId , B.REQ_STAFF_ID reqStaffId , A.SELL_STAFF_TEL reqTel , C.STAFF_NAME reqStaffName , C.MOBILE reqStaffTel , CASE WHEN A.SELL_CUST_ID = B.REQ_CUST_ID AND A.SELL_DEPT_ID = B.REQ_DEPT_ID THEN B.REQ_ADDR ELSE '' END reqAddr , CASE WHEN A.SELL_CUST_ID = B.REQ_CUST_ID AND A.SELL_DEPT_ID = B.REQ_DEPT_ID THEN B.REQ_ADDR_DETAIL ELSE '' END reqAddrDetail , B.S_COM_NAME sComName , B.S_SIDO sSido , B.S_GUNGU sGungu , B.S_DONG sDong , B.S_ADDR sAddr , B.S_ADDR_DETAIL sAddrDetail , CONVERT(CHAR(10),B.S_DATE, 23) sDateDay , CONVERT(CHAR(10),B.S_DATE, 24) sDateTime , B.S_STAFF sStaff , B.S_TEL sTel , B.E_COM_NAME eComName , B.E_SIDO eSido , B.E_GUNGU eGungu , B.E_DONG eDong , B.E_ADDR eAddr , B.E_ADDR_DETAIL eAddrDetail , CONVERT(CHAR(10),B.E_DATE, 23) eDateDay , CONVERT(CHAR(10),B.E_DATE, 24) eDateTime , B.E_STAFF eStaff , B.E_TEL eTel , B.GOODS_NAME goodsName , CONVERT(FLOAT,B.GOODS_WEIGHT)/1000 goodsWeight , B.MIX_YN mixYn , B.RETURN_YN returnYn , B.CAR_TON_CODE carTonCode , B.CAR_TYPE_CODE carTypeCode , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CAR_TON_CD' AND CODE = B.CAR_TON_CODE) orderCarTonName
, (SELECT CNAME FROM T_CODE WHERE GCODE = 'CHARGE_TYPE_CD' AND CODE = B.CHARGE_TYPE) chargeType , B.REGID regid , B.CUST_ID custId , B.DEPT_ID deptId , B.ORDER_STATE orderState , CONVERT(CHAR(19), B.REGDATE, 20) regdate , CONVERT(CHAR(19), B.ALLOC_DATE, 20) allocDate , B.MEMO , A.ALLOC_ID sellAllocId , A.SELL_CUST_ID sellCustId , A.SELL_DEPT_ID sellDeptId , A.SELL_STAFF_ID sellStaffId , A.ALLOC_CHARGE sellCharge , A.SELL_WEIGHT sellWeight , A.ALLOC_FEE sellFee , A.LINK_YN sellLinkYn , C.CUST_NAME sellCustName , D.ALLOC_ID allocId , D.BUY_CUST_ID buyCustId , D.BUY_DEPT_ID buyDeptId , D.BUY_STAFF_ID buyStaffId , D.BUY_STAFF_TEL buyStaffTel , D.ALLOC_CHARGE buyCharge , D.ALLOC_FEE buyFee , D.DRIVER_ID buyDriverId , D.VEHIC_ID buyVehicId , D.DRIVER_NAME buyDriverName , D.DRIVER_TEL buyDriverTel , D.CAR_NUM buyCarNum , ISNULL(D.LINK_YN, 'N') buyLinkYn , E.CUST_NAME buyCustName , ISNULL(D.ALLOC_STATE, '00') allocState
, CASE WHEN B.ORDER_STATE = '09' THEN '취소' WHEN ISNULL(F.ALLOC_STATE, '') = '' THEN (SELECT CNAME FROM T_CODE WHERE GCODE = 'ALLOC_STATE_CD' AND CODE = ISNULL(D.ALLOC_STATE, '00')) ELSE (SELECT CNAME FROM T_CODE WHERE GCODE = 'ALLOC_STATE_CD' AND CODE = F.ALLOC_STATE) END allocStateName , CASE WHEN ISNULL(D.BUY_CUST_ID, '') = '' THEN 'D' ELSE 'C' END allocKind , CASE WHEN ISNULL(F.DRIVER_ID, '') = '' THEN 'N' ELSE 'Y' END driverKind , F.ALLOC_STATE driverState , F.DRIVER_NAME driverName , F.DRIVER_TEL driverTel , F.CAR_NUM carNum , (SELECT CNAME FROM T_CODE WHERE GCODE = 'ALLOC_STATE_CD' AND CODE = F.ALLOC_STATE) driverStateName , ISNULL(G.STOP_COUNT, 0) stopCount , CASE WHEN ISNULL(G.STOP_COUNT, 0) = 0 THEN 'N' ELSE 'Y' END stopYn , (SELECT CNAME FROM T_CODE WHERE GCODE = 'ORDER_STATE_CD' AND CODE = B.ORDER_STATE) orderStateName , (SELECT DEPT_NAME FROM T_CUST_DEPT WHERE DEPT_ID = A.BUY_DEPT_ID) deptName , (SELECT CNAME FROM T_CODE WHERE GCODE = 'LINK_CD' AND CODE = D.LINK_CODE ) linkName , A.BUY_STAFF_ID staffId , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CAR_MNG_CD' AND CODE = H.CAR_MNG_CODE) carMngName , H.CAR_MNG_MEMO carMngMemo , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CAR_MNG_CD' AND CODE = C.CUST_MNG_CODE) custMngName , C.CUST_MNG_MEMO custMngMemo , ISNULL(D.LINK_SETTLE_YN,'N') linkSettleYn , I.ADD_CHARGE sellWayPointCharge , J.ADD_CHARGE sellStayCharge , K.ADD_CHARGE sellHandWorkCharge , L.ADD_CHARGE sellRoundCharge , M.ADD_CHARGE sellOtherAddCharge , N.ADD_CHARGE wayPointCharge , O.ADD_CHARGE stayCharge , P.ADD_CHARGE handWorkCharge , Q.ADD_CHARGE roundCharge , R.ADD_CHARGE otherAddCharge , B.S_LAT sLat , B.S_LON sLon , B.E_LAT eLat , B.E_LON eLon , D.PAY_TYPE payType , S.USER_NAME staffName , REPLACE(CONVERT(CHAR(5), D.REQ_PAY_DATE, 10), '-' ,'.') reqPayDate , ISNULL(A.ALLOC_CHARGE, 0) + ISNULL(I.ADD_CHARGE, 0) + ISNULL(J.ADD_CHARGE, 0) + ISNULL(K.ADD_CHARGE, 0) + ISNULL(L.ADD_CHARGE, 0) + ISNULL(M.ADD_CHARGE, 0) sellAmt , ISNULL(D.ALLOC_CHARGE, 0) + ISNULL(N.ADD_CHARGE, 0) + ISNULL(O.ADD_CHARGE, 0) + ISNULL(P.ADD_CHARGE, 0) + ISNULL(Q.ADD_CHARGE, 0) + ISNULL(R.ADD_CHARGE, 0) buyAmt , M.MEMO sellOtherAddMemo , R.MEMO otherAddMemo , T.USER_NAME regname , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CARGO_TRAN_CAR_SCTN_CD' AND CODE = H.CAR_SCTN_CODE) carSctnName , ISNULL(REPLACE(CONVERT(CHAR(5), B.RECEIPT_DATE, 10), '-' ,'.'), 'N') + '/' + ISNULL(REPLACE(CONVERT(CHAR(5), B.PAPER_RECEIPT_DATE, 10), '-' ,'.'), 'N') receiptYn
FROM ( SELECT ORDER_ID , ALLOC_ID , SELL_CUST_ID , SELL_DEPT_ID , SELL_STAFF_ID , BUY_CUST_ID , BUY_DEPT_ID , BUY_STAFF_ID , ALLOC_CHARGE , ALLOC_FEE , SELL_STAFF_TEL , LINK_YN , SELL_WEIGHT FROM T_ORDER_ALLOC WHERE BUY_CUST_ID = #{custId} AND BUY_DEPT_ID = #{deptId} AND BUY_STAFF_ID = #{userId} ) A INNER JOIN T_ORDER B ON A.ORDER_ID = B.ORDER_ID INNER JOIN (SELECT CUST_ID , DEPT_ID, CUST_NAME, MNG_DEPT_ID, MOBILE, STAFF_NAME, CUST_MNG_CODE, CUST_MNG_MEMO FROM T_CUST_DTL WHERE MNG_CUST_ID = #{custId} AND SELL_BUY_SCTN = '01') C ON A.SELL_CUST_ID = C.CUST_ID AND A.SELL_DEPT_ID = C.DEPT_ID AND A.BUY_DEPT_ID = C.MNG_DEPT_ID LEFT OUTER JOIN T_ORDER_ALLOC D ON A.ORDER_ID = D.ORDER_ID AND A.BUY_CUST_ID = D.SELL_CUST_ID AND A.BUY_DEPT_ID = D.SELL_DEPT_ID LEFT OUTER JOIN (SELECT CUST_ID, DEPT_ID, CUST_NAME, MNG_DEPT_ID FROM T_CUST_DTL WHERE MNG_CUST_ID = #{custId} AND SELL_BUY_SCTN = '02') E ON D.BUY_CUST_ID = E.CUST_ID AND D.BUY_DEPT_ID = E.DEPT_ID AND A.BUY_DEPT_ID = E.MNG_DEPT_ID LEFT OUTER JOIN T_ORDER_ALLOC F ON A.ORDER_ID = F.ORDER_ID AND ISNULL(F.DRIVER_ID, '') != '' AND ISNULL(F.VEHIC_ID, '') != '' LEFT OUTER JOIN (SELECT ORDER_ID, COUNT(*) STOP_COUNT FROM T_ORDER_STOP WHERE USE_YN = 'Y' GROUP BY ORDER_ID) G ON B.ORDER_ID = G.ORDER_ID LEFT OUTER JOIN T_CUST_VEHIC H ON H.DRIVER_ID = F.DRIVER_ID AND H.VEHIC_ID = F.VEHIC_ID AND H.CUST_ID = F.SELL_CUST_ID AND H.DEPT_ID = F.SELL_DEPT_ID LEFT OUTER JOIN T_ORDER_ALLOC_ADD I ON A.ORDER_ID = I.ORDER_ID AND A.ALLOC_ID = I.ALLOC_ID AND I.CALC_TYPE_CODE = '0023' LEFT OUTER JOIN T_ORDER_ALLOC_ADD J ON A.ORDER_ID = J.ORDER_ID AND A.ALLOC_ID = J.ALLOC_ID AND J.CALC_TYPE_CODE = '0025' LEFT OUTER JOIN T_ORDER_ALLOC_ADD K ON A.ORDER_ID = K.ORDER_ID AND A.ALLOC_ID = K.ALLOC_ID AND K.CALC_TYPE_CODE = '0371' LEFT OUTER JOIN T_ORDER_ALLOC_ADD L ON A.ORDER_ID = L.ORDER_ID AND A.ALLOC_ID = L.ALLOC_ID AND L.CALC_TYPE_CODE = '0372' LEFT OUTER JOIN T_ORDER_ALLOC_ADD M ON A.ORDER_ID = M.ORDER_ID AND A.ALLOC_ID = M.ALLOC_ID AND M.CALC_TYPE_CODE = '0294' LEFT OUTER JOIN T_ORDER_ALLOC_ADD N ON D.ORDER_ID = N.ORDER_ID AND D.ALLOC_ID = N.ALLOC_ID AND N.CALC_TYPE_CODE = '0023' LEFT OUTER JOIN T_ORDER_ALLOC_ADD O ON D.ORDER_ID = O.ORDER_ID AND D.ALLOC_ID = O.ALLOC_ID AND O.CALC_TYPE_CODE = '0025' LEFT OUTER JOIN T_ORDER_ALLOC_ADD P ON D.ORDER_ID = P.ORDER_ID AND D.ALLOC_ID = P.ALLOC_ID AND P.CALC_TYPE_CODE = '0371' LEFT OUTER JOIN T_ORDER_ALLOC_ADD Q ON D.ORDER_ID = Q.ORDER_ID AND D.ALLOC_ID = Q.ALLOC_ID AND Q.CALC_TYPE_CODE = '0372' LEFT OUTER JOIN T_ORDER_ALLOC_ADD R ON D.ORDER_ID = R.ORDER_ID AND D.ALLOC_ID = R.ALLOC_ID AND R.CALC_TYPE_CODE = '0294' LEFT OUTER JOIN T_USER S ON D.REGID = S.USER_ID INNER JOIN T_USER T ON B.REGID = T.USER_ID WHERE 1=1
AND B.REQ_CUST_ID = #{reqCustId} AND A.SELL_CUST_ID = #{reqCustId}
AND B.REQ_DEPT_ID = #{reqDeptId}
