SELECT COUNT(1) retCnt , ISNULL(SUM(CASE WHEN B.ORDER_STATE IN ('00') THEN 1 ELSE 0 END),'00') registerOrderCount , ISNULL(SUM(CASE WHEN B.ORDER_STATE IN ('01', '04', '05', '12') THEN 1 ELSE 0 END),'00') dispatchOrderCount , ISNULL(SUM(CASE WHEN B.ORDER_STATE IN ('09') THEN 1 ELSE 0 END),'00') cancelOrderCount , ISNULL(SUM(CASE WHEN B.ORDER_STATE IN ('01', '04', '05', '12') THEN CONVERT(BIGINT, CASE WHEN B.CHARGE_TYPE = '01' THEN A.ALLOC_CHARGE ELSE A.ALLOC_FEE END)+ISNULL(L.ADD_CHARGE,'0') ELSE 0 END),'0') sumSellCharge , ISNULL(SUM(CASE WHEN B.ORDER_STATE IN ('01', '04', '05', '12') THEN CONVERT(BIGINT, CASE WHEN B.CHARGE_TYPE = '01' THEN K.ALLOC_CHARGE ELSE 0 END)+ISNULL(N.ADD_CHARGE,'0') ELSE 0 END),'0') sumBuyCharge , ISNULL(SUM(CASE WHEN B.ORDER_STATE IN ('01', '04', '05', '12') THEN (CONVERT(BIGINT, CASE WHEN B.CHARGE_TYPE = '01' THEN A.ALLOC_CHARGE ELSE A.ALLOC_FEE END)+ISNULL(L.ADD_CHARGE,'0')) - (CONVERT(BIGINT, CASE WHEN B.CHARGE_TYPE = '01' THEN K.ALLOC_CHARGE ELSE 0 END)+ISNULL(N.ADD_CHARGE,'0')) ELSE 0 END),'0') profitAndLoss FROM ( SELECT ORDER_ID , ALLOC_ID , SELL_CUST_ID , SELL_DEPT_ID , SELL_STAFF_ID , BUY_CUST_ID , BUY_DEPT_ID , BUY_STAFF_ID , REGDATE , REGID , LINK_SETTLE_YN, ALLOC_CHARGE , ALLOC_FEE FROM T_ORDER_ALLOC WHERE BUY_CUST_ID = #{custId} AND BUY_DEPT_ID = #{deptId} AND BUY_STAFF_ID = #{userId} ) A INNER JOIN T_ORDER B ON A.ORDER_ID = B.ORDER_ID INNER JOIN (SELECT CUST_ID , DEPT_ID, CUST_NAME, MNG_DEPT_ID, CUST_MNG_CODE FROM T_CUST_DTL WHERE MNG_CUST_ID = #{custId} AND SELL_BUY_SCTN = '01') C ON A.SELL_CUST_ID = C.CUST_ID AND A.SELL_DEPT_ID = C.DEPT_ID AND A.BUY_DEPT_ID = C.MNG_DEPT_ID
INNER JOIN T_ORDER_ALLOC D ON A.ORDER_ID = D.ORDER_ID AND A.BUY_CUST_ID = D.SELL_CUST_ID AND A.BUY_DEPT_ID = D.SELL_DEPT_ID
AND ISNULL(B.ORDER_STATE, '00') = #{sAllocState}
INNER JOIN (SELECT CUST_ID, DEPT_ID, CUST_NAME, MNG_DEPT_ID FROM T_CUST_DTL WHERE MNG_CUST_ID = #{custId} AND SELL_BUY_SCTN = '02' AND CUST_NAME LIKE '%' + #{searchValue} + '%') E ON D.BUY_CUST_ID = E.CUST_ID AND D.BUY_DEPT_ID = E.DEPT_ID AND A.BUY_DEPT_ID = E.MNG_DEPT_ID
INNER JOIN T_ORDER_ALLOC F ON A.ORDER_ID = F.ORDER_ID AND ISNULL(F.DRIVER_ID, '') != '' AND ISNULL(F.VEHIC_ID, '') != ''
AND ISNULL(F.ALLOC_STATE, '00') = #{driverState}
AND F.CAR_NUM LIKE '%' + #{driverValue} + '%'
AND F.DRIVER_NAME LIKE '%' + #{driverValue} + '%'
LEFT OUTER JOIN (SELECT ORDER_ID, COUNT(*) STOP_COUNT FROM T_ORDER_STOP WHERE USE_YN = 'Y' GROUP BY ORDER_ID) G ON B.ORDER_ID = G.ORDER_ID
INNER JOIN T_ORDER_ALLOC H ON A.ORDER_ID = H.ORDER_ID AND ISNULL(H.DRIVER_ID, '') != '' AND ISNULL(H.VEHIC_ID, '') != '' INNER JOIN T_CUST_VEHIC I ON H.DRIVER_ID = H.DRIVER_ID AND I.VEHIC_ID = H.VEHIC_ID AND I.CUST_ID = H.SELL_CUST_ID AND I.DEPT_ID = H.SELL_DEPT_ID AND I.CAR_MNG_CODE = #{sCarMngCode}
LEFT OUTER JOIN T_ORDER_ALLOC J ON A.ORDER_ID = J.ORDER_ID AND A.BUY_CUST_ID = J.SELL_CUST_ID AND A.BUY_DEPT_ID = J.SELL_DEPT_ID
LEFT OUTER JOIN T_ORDER_ALLOC K ON A.ORDER_ID = K.ORDER_ID AND A.BUY_CUST_ID = K.SELL_CUST_ID AND A.BUY_DEPT_ID = K.SELL_DEPT_ID LEFT OUTER JOIN (SELECT SUM(ADD_CHARGE) ADD_CHARGE, ALLOC_ID FROM T_ORDER_ALLOC_ADD GROUP BY ALLOC_ID) L ON A.ALLOC_ID = L.ALLOC_ID LEFT OUTER JOIN (SELECT SUM(ADD_CHARGE) ADD_CHARGE, ALLOC_ID FROM T_ORDER_ALLOC_ADD GROUP BY ALLOC_ID) N ON K.ALLOC_ID = N.ALLOC_ID WHERE 1=1
AND B.REQ_CUST_ID = #{reqCustId} AND A.SELL_CUST_ID = #{reqCustId}
AND B.REQ_DEPT_ID = #{reqDeptId}
AND B.S_SIDO = #{listSSido}
AND B.E_SIDO = #{listESido}
AND C.CUST_MNG_CODE = #{sCustMngCode}
AND B.ORDER_STATE != '09'
AND C.CUST_NAME LIKE '%' + #{searchValue} + '%'
AND A.ORDER_ID = #{searchValue}
AND B.S_COM_NAME LIKE '%' + #{searchValue} + '%'
AND B.E_COM_NAME LIKE '%' + #{searchValue} + '%'
AND B.MIX_YN = #{sMixYn}
AND CASE WHEN ISNULL(G.STOP_COUNT, 0) = 0 THEN 'N' ELSE 'Y' END = #{orderStopYn}
AND J.LINK_SETTLE_YN = #{sLinkSettleYn}
AND B.REGDATE BETWEEN #{fromDate} AND #{toDate} + ' 23:59:59'
AND B.S_DATE BETWEEN #{fromDate} AND #{toDate} + ' 23:59:59'
AND B.REGID = #{loginId}

