MERGE INTO T_ORDER_STOP T USING (
SELECT #{orderId} AS ORDER_ID , #{item.stopSeq, jdbcType=INTEGER} AS STOP_SEQ , (#{index} + 1) AS STOP_NO , #{item.eComName} AS E_COM_NAME , #{item.eAddr} AS E_ADDR , #{item.eAddrDetail} AS E_ADDR_DETAIL , #{item.eStaff} AS E_STAFF , REPLACE(#{item.eTel}, '-', '') AS E_TEL , CONVERT(float, #{item.goodsWeight}) * 1000 AS GOODS_WEIGHT , #{item.eLat, jdbcType=VARCHAR} AS E_LAT , #{item.eLon, jdbcType=VARCHAR} AS E_LON , #{item.weightUnitCode} AS WEIGHT_UNIT_CODE , #{item.goodsQty, jdbcType=INTEGER} AS GOODS_QTY , #{item.qtyUnitCode} AS QTY_UNIT_CODE , #{item.goodsName} AS GOODS_NAME , #{item.stopSe} AS STOP_SE , CASE WHEN #{item.useYn} = 'N' THEN 'N' ELSE 'Y' END AS USE_YN
) AS S ON (T.STOP_SEQ = S.STOP_SEQ) WHEN MATCHED THEN UPDATE SET T.STOP_NO = S.STOP_NO , T.E_COM_NAME = S.E_COM_NAME , T.E_ADDR = S.E_ADDR , T.E_ADDR_DETAIL = S.E_ADDR_DETAIL , T.E_STAFF = S.E_STAFF , T.E_TEL = S.E_TEL , T.GOODS_WEIGHT = S.GOODS_WEIGHT , T.E_LAT = S.E_LAT , T.E_LON = S.E_LON , T.WEIGHT_UNIT_CODE = S.WEIGHT_UNIT_CODE , T.GOODS_QTY = S.GOODS_QTY , T.QTY_UNIT_CODE = S.QTY_UNIT_CODE , T.GOODS_NAME = S.GOODS_NAME , T.STOP_SE = S.STOP_SE , T.USE_YN = S.USE_YN WHEN NOT MATCHED THEN INSERT ( ORDER_ID , STOP_NO , E_COM_NAME , E_ADDR , E_ADDR_DETAIL , E_STAFF , E_TEL , E_LAT , E_LON , GOODS_WEIGHT , WEIGHT_UNIT_CODE , GOODS_QTY , QTY_UNIT_CODE , GOODS_NAME , STOP_SE ) VALUES ( S.ORDER_ID , S.STOP_NO , S.E_COM_NAME , S.E_ADDR , S.E_ADDR_DETAIL , S.E_STAFF , S.E_TEL , S.E_LAT , S.E_LON , S.GOODS_WEIGHT , S.WEIGHT_UNIT_CODE , S.GOODS_QTY , S.QTY_UNIT_CODE , S.GOODS_NAME , S.STOP_SE );

