MERGE INTO T_ORDER_ALLOC_ADD A USING (
SELECT #{orderId} AS ORDER_ID , #{allocId} AS ALLOC_ID , #{item.code, jdbcType=NVARCHAR} AS CALC_TYPE_CODE , #{item.charge, jdbcType=NVARCHAR} AS ADD_CHARGE , REPLACE(#{item.memo, jdbcType=NVARCHAR}, ' ', '') AS MEMO , GETDATE() AS GETDATE , #{loginId} AS LOGIN_USER
) AS B ON (A.ALLOC_ID = B.ALLOC_ID AND A.CALC_TYPE_CODE = B.CALC_TYPE_CODE) WHEN MATCHED THEN UPDATE SET A.CALC_TYPE_CODE = B.CALC_TYPE_CODE , A.ADD_CHARGE = B.ADD_CHARGE , A.MEMO = B.MEMO , A.EDITDATE = B.GETDATE , A.EDITID = B.LOGIN_USER , A.ALLOC_ID = B.ALLOC_ID WHEN NOT MATCHED AND B.ADD_CHARGE != 0 THEN INSERT ( ORDER_ID , ALLOC_ID , CALC_TYPE_CODE , ADD_CHARGE , MEMO , REGDATE , REGID ) VALUES ( B.ORDER_ID , B.ALLOC_ID , B.CALC_TYPE_CODE , B.ADD_CHARGE , B.MEMO , B.GETDATE , B.LOGIN_USER );

