WHERE S_SIDO = #{sSido}
AND S_GUNGU = #{sGungu}
AND E_SIDO = #{eSido}
AND E_GUNGU = #{eGungu}
AND TON_CODE = #{carTonCode}

SELECT ROW_NUMBER() OVER(ORDER BY AAA.carNum ASC) number , AAA.* , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CAR_TON_CD' AND CODE = AAA.CAR_TON_CODE) carTonName , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CAR_TYPE_CD' AND CODE = AAA.CAR_TYPE_CODE) carTypeName , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CARGO_TRAN_CAR_SCTN_CD' AND CODE = AAA.CAR_SCTN_CODE) carSctnName FROM ( SELECT B.CAR_NUM carNum, C.MOBILE driverTel , C.DRIVER_NAME driverName , B.CAR_TON_CODE, B.CAR_TYPE_CODE, A.CAR_SCTN_CODE, STUFF((SELECT '!@' + BB.S_SIDO+' '+BB.S_GUNGU + '|' + BB.E_SIDO+' '+BB.E_GUNGU + '|'+ EE.CUST_NAME +'|' + CONVERT(CHAR(10), BB.S_DATE, 23) + '|' + CONVERT(CHAR(2), BB.S_DATE, 8) + '|' + CONVERT(CHAR(10), BB.E_DATE, 23) + '|' + CONVERT(CHAR(2), BB.E_DATE, 8) + '|' + BB.ORDER_STATE + '|' + EE.CUST_NAME + '|' + BB.ORDER_ID + '|' + BB.DATE_CHK + '|' + BB.RETURN_CHK + '|' + CASE WHEN DD.LINK_CODE != '99' THEN (SELECT CNAME FROM T_CODE WHERE GCODE = 'LINK_CD' AND CODE = DD.LINK_CODE) ELSE '' END FROM (SELECT ORDER_ID, ALLOC_ID, SELL_CUST_ID, SELL_DEPT_ID, DRIVER_ID, VEHIC_ID FROM T_ORDER_ALLOC WHERE SELL_CUST_ID = #{custId} AND ALLOC_STATE IN ('01', '12', '04', '05')) AA INNER JOIN (SELECT ORDER_ID, S_SIDO, S_GUNGU, E_SIDO, E_GUNGU, S_DATE, E_DATE, ORDER_STATE, S_COM_NAME, E_COM_NAME, CASE WHEN CONVERT(CHAR(8), S_DATE, 112) = CONVERT(CHAR(8), E_DATE, 112) THEN '당착' ELSE '익착' END DATE_CHK, CASE WHEN RETURN_YN = 'Y' THEN '왕복' ELSE '편도' END RETURN_CHK FROM T_ORDER WHERE (S_DATE BETWEEN CONVERT(CHAR(10), GETDATE(), 23) AND CONVERT(CHAR(10), GETDATE(), 23) + ' 23:59:59') OR (E_DATE BETWEEN CONVERT(CHAR(10), GETDATE(), 23) AND CONVERT(CHAR(10), GETDATE(), 23) + ' 23:59:59')) BB ON AA.ORDER_ID = BB.ORDER_ID AND AA.DRIVER_ID = A.DRIVER_ID AND AA.VEHIC_ID = A.VEHIC_ID INNER JOIN T_ORDER_ALLOC CC ON AA.SELL_CUST_ID = CC.BUY_CUST_ID AND AA.SELL_DEPT_ID = CC.BUY_DEPT_ID AND AA.ORDER_ID = CC.ORDER_ID INNER JOIN T_ORDER_ALLOC DD ON BB.ORDER_ID = DD.ORDER_ID AND ISNULL(DD.DRIVER_ID, '') != '' AND ISNULL(DD.VEHIC_ID, '') != '' INNER JOIN T_CUST_DTL EE ON CC.SELL_CUST_ID = EE.CUST_ID AND CC.SELL_DEPT_ID = EE.DEPT_ID AND CC.BUY_CUST_ID = EE.MNG_CUST_ID AND CC.BUY_DEPT_ID = EE.MNG_DEPT_ID ORDER BY BB.S_DATE ASC FOR XML PATH('') ), 1, 2, '') AS orderInfo FROM ( SELECT CUST_ID, VEHIC_ID, DRIVER_ID, MIN(CAR_SCTN_CODE) CAR_SCTN_CODE FROM T_CUST_VEHIC WHERE CUST_ID = #{custId} AND USE_YN = 'Y' AND CAR_SCTN_CODE IN
#{item.carSctnCode}
AND DEPT_ID = #{deptId}
AND MANAGER = #{userId}
GROUP BY CUST_ID, VEHIC_ID, DRIVER_ID ) A INNER JOIN T_VEHIC_MST B ON A.VEHIC_ID = B.VEHIC_ID AND A.DRIVER_ID = B.DRIVER_ID INNER JOIN T_DRIVER C ON B.DRIVER_ID = C.DRIVER_ID ) AAA ORDER BY AAA.carNum ASC

