FROM T_ORDER_HISTORY
WHERE ORDER_ID = #{orderId}

SELECT AAA.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY AA.sDate DESC, AA.eDate DESC, AA.orderId) number , AA.* FROM ( SELECT CASE WHEN ISNULL(C.FINISH_YN,'N') = 'N' THEN ISNULL(C.FINISH_YN,'N') ELSE REPLACE(CONVERT(CHAR(5), C.CALC_DATE, 10), '-' ,'.') END sellFinishDate , CASE WHEN ISNULL(D.FINISH_YN,'N') = 'N' THEN ISNULL(D.FINISH_YN,'N') ELSE REPLACE(CONVERT(CHAR(5), D.CALC_DATE, 10), '-' ,'.') END buyFinishDate , B.S_DATE sDate , REPLACE(CONVERT(CHAR(5), B.S_DATE, 10),'-','.') sDateDay , B.E_DATE eDate , REPLACE(CONVERT(CHAR(5), B.E_DATE, 10),'-','.') eDateDay , E.CUST_NAME reqCustName , B.S_ADDR sAddr , B.E_ADDR eAddr , ISNULL(F.STOP_COUNT, 0) stopCount , (SELECT TOP 1 E_COM_NAME FROM T_ORDER_STOP WHERE ORDER_ID = A.ORDER_ID AND USE_YN = 'Y' ORDER BY STOP_SEQ ASC) stopName , CASE WHEN ISNULL(B.RETURN_YN,'N') = 'N' THEN '편도' ELSE '왕복' END returnYn , ISNULL(ISNULL(A.ALLOC_CHARGE, 0) + ISNULL(H.ADD_CHARGE, 0) + ISNULL(I.ADD_CHARGE, 0) + ISNULL(J.ADD_CHARGE, 0) + ISNULL(K.ADD_CHARGE, 0) + ISNULL(L.ADD_CHARGE, 0),0) sellAmt , ISNULL(G.ALLOC_CHARGE, 0) + ISNULL(M.ADD_CHARGE, 0) + ISNULL(N.ADD_CHARGE, 0) + ISNULL(O.ADD_CHARGE, 0) + ISNULL(P.ADD_CHARGE, 0) + ISNULL(Q.ADD_CHARGE, 0) buyAmt , ISNULL(A.ALLOC_CHARGE, 0) sellCharge , ISNULL(H.ADD_CHARGE, 0) sellWayPointCharge , ISNULL(I.ADD_CHARGE, 0) sellStayCharge , ISNULL(J.ADD_CHARGE, 0) sellHandWorkCharge , ISNULL(K.ADD_CHARGE, 0) sellRoundCharge , ISNULL(L.ADD_CHARGE, 0) sellOtherAddCharge , (SELECT CNAME FROM T_CODE WHERE GCODE = 'UNIT_PRICE_TYPE' AND CODE = B.UNIT_PRICE_TYPE) unitPriceType , ISNULL(B.UNIT_CHARGE, 0) unitCharge , ISNULL(A.SELL_WEIGHT, '') sellWeight , REPLACE(CONVERT(CHAR(5), G.REQ_PAY_DATE, 10), '-' ,'.') reqPayDate , ISNULL(G.ALLOC_CHARGE, 0) buyCharge , ISNULL(M.ADD_CHARGE, 0) wayPointCharge , ISNULL(N.ADD_CHARGE, 0) stayCharge , ISNULL(O.ADD_CHARGE, 0) handWorkCharge , ISNULL(P.ADD_CHARGE, 0) roundCharge , ISNULL(Q.ADD_CHARGE, 0) otherAddCharge , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CARGO_TRAN_CAR_SCTN_CD' AND CODE = R.CAR_SCTN_CODE) carSctnName , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CHARGE_TYPE_CD' AND CODE = B.CHARGE_TYPE) chargeType , U.CAR_NUM carNum , U.DRIVER_NAME driverName , U.DRIVER_TEL driverTel , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CAR_TON_CD' AND CODE = G.CAR_TON_CODE) carTonName , (SELECT CNAME FROM T_CODE WHERE GCODE = 'CAR_TYPE_CD' AND CODE = G.CAR_TYPE_CODE) carTypeName , S.CUST_NAME buyCustName , T.INV_ID invId , ISNULL(B.RECEIPT_YN, 'N') receiptYn , ISNULL(REPLACE(CONVERT(CHAR(5), B.RECEIPT_DATE, 10), '-' ,'.'), '-') + '/' + ISNULL(REPLACE(CONVERT(CHAR(5), B.PAPER_RECEIPT_DATE, 10), '-' ,'.'), '-') receiptDate , ISNULL(D.TAXINV_YN, 'N') taxinvYn , CASE WHEN D.TAXINV_YN = 'P' THEN '-/'+ REPLACE(CONVERT(CHAR(5), D.TAXINV_DATE, 10), '-', '.') WHEN D.TAXINV_YN = 'Y' THEN REPLACE(CONVERT(CHAR(5), D.TAXINV_DATE, 10), '-', '.')+'/-' ELSE '-/-' END taxinvDate , CASE WHEN B.ORDER_STATE = '09' THEN '취소' WHEN ISNULL(U.ALLOC_STATE, '') = '' THEN (SELECT CNAME FROM T_CODE WHERE GCODE = 'ALLOC_STATE_CD' AND CODE = ISNULL(U.ALLOC_STATE, '00')) ELSE (SELECT CNAME FROM T_CODE WHERE GCODE = 'ALLOC_STATE_CD' AND CODE = U.ALLOC_STATE) END allocStateName , B.ORDER_ID orderId , A.ALLOC_ID sellAllocId , G.ALLOC_ID allocId , CASE WHEN G.ALLOC_STATE != '10' THEN G.CAR_NUM ELSE U.CAR_NUM END buyCarNum , A.SELL_CUST_ID sellCustId , A.SELL_DEPT_ID sellDeptId , A.BUY_CUST_ID buyCustId , A.BUY_DEPT_ID buyDeptId FROM ( SELECT ORDER_ID , ALLOC_ID , SELL_CUST_ID , SELL_DEPT_ID , SELL_STAFF_ID , BUY_CUST_ID , BUY_DEPT_ID , BUY_STAFF_ID , ALLOC_CHARGE , ALLOC_FEE , SELL_STAFF_TEL , LINK_YN , SELL_WEIGHT FROM T_ORDER_ALLOC WHERE BUY_CUST_ID = #{custId} AND BUY_DEPT_ID = #{deptId} AND BUY_STAFF_ID = #{userId}
