mapper.select:
SELECT
	TOP 1 B.ALLOC_CHARGE FROM dbo.T_ORDER AS a
								  INNER JOIN
							  T_ORDER_ALLOC AS B ON A.order_id =B.order_id
WHERE 1=1

  	AND
    	A.REQ_CUST_ID  = #{reqCustId}


	AND
		A.REQ_DEPT_ID = #{reqDeptId}


	AND
		A.S_SIDO = #{sSido}


	AND
		A.S_GUNGU = #{sGungu}


	AND
		A.E_SIDO = #{eSido}


	AND
		A.E_GUNGU = #{eGungu}


	AND
		A.CAR_TYPE_CODE = #{carTypeCode}


	AND
		A.CAR_TON_CODE = #{carTonCode}


	AND
		A.CUST_ID = #{custId}

	AND
		B.ALLOC_CHARGE != 0

ORDER BY B.REGDATE DESC, B.ALLOC_ID DESC
