mapper.select:
SELECT A.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY AA.writeDate DESC) AS number , AA.* FROM ( SELECT AAA.INV_ID invId , AAA.BILL_KIND billKind , CASE WHEN AAA.SUPPLIER_CUST_ID = #{loginCustId} THEN 'S' ELSE 'R' END pubType , AAA.ISSUE_DATE issueDate , AAA.ISSUE_ID issueId , AAA.DOC_CLASS docClass , AAA.DOC_TYPE docType , AAA.REMARKS , AAA.REMARKS2 , AAA.REMARKS3 , AAA.WRITE_DATE writeDate , AAA.PUBFORM , AAA.SUPPLIER_BIZ_NO supplierBizNo , AAA.SUPPLIER_BIZ_TYPE supplierBizType , AAA.SUPPLIER_BIZ_NAME supplierBizName , AAA.SUPPLIER_BIZ_CLASS supplierBizClass , CASE WHEN AAA.SUPPLIER_BIZ_SUBNO = '0000' THEN '' ELSE ISNULL(AAA.SUPPLIER_BIZ_SUBNO, '') END supplierBizSubno , AAA.SUPPLIER_CEO supplierCeo , AAA.SUPPLIER_MEM_NAME supplierMemName , AAA.SUPPLIER_MEM_TEL supplierMemTel , AAA.SUPPLIER_MEM_EMAIL supplierMemEmail , AAA.SUPPLIER_ADDR supplierAddr , AAA.SUPPLIER_CUST_ID supplierCustId , AAA.SUPPLIER_DEPT_ID supplierDeptId , AAA.SUPPLIER_VEHIC_ID supplierVehicId , AAA.SUPPLIER_DRIVER_ID supplierDriverId , AAA.BUYER_CUST_ID buyerCustId , AAA.BUYER_DEPT_ID buyerDeptId , AAA.BUYER_BIZ_NO buyerBizNo , AAA.BUYER_BIZ_TYPE buyerBizType , AAA.BUYER_BIZ_NAME buyerBizName , AAA.BUYER_BIZ_CLASS buyerBizClass , CASE WHEN AAA.BUYER_BIZ_SUBNO = '0000' THEN '' ELSE ISNULL(AAA.BUYER_BIZ_SUBNO, '') END buyerBizSubno , AAA.BUYER_BIZ_NO_TYPE buyerBizNoType , AAA.BUYER_CEO buyerCeo , AAA.BUYER_MEM_NAME buyerMemName , AAA.BUYER_MEM_TEL buyerMemTel , AAA.BUYER_MEM_EMAIL buyerMemEmail , AAA.BUYER_ADDR buyerAddr , AAA.BROKER_CUST_ID brokerCustId , AAA.BROKER_DEPT_ID brokerDeptId , AAA.BROKER_BIZ_NO brokerBizNo , AAA.BROKER_BIZ_TYPE brokerBizType , AAA.BROKER_BIZ_NAME brokerBizName , AAA.BROKER_BIZ_CLASS brokerBizClass , CASE WHEN AAA.BROKER_BIZ_SUBNO = '0000' THEN '' ELSE ISNULL(AAA.BROKER_BIZ_SUBNO, '') END brokerBizSubno , AAA.BROKER_CEO brokerCeo , AAA.BROKER_MEM_NAME brokerMemName , AAA.BROKER_MEM_TEL brokerMemTel , AAA.BROKER_MEM_EMAIL brokerMemEmail , AAA.BROKER_ADDR brokerAddr , AAA.CASH , AAA.CHEQUE , AAA.BILL , AAA.OUTSTAND , AAA.SUPPRICE , AAA.TAX , AAA.TOT_PRICE totPrice , AAA.LOADSTATUS loadStatus , (SELECT CNAME FROM SB_TAX_CODE WHERE GCODE = 'DTI_STATUS' AND CODE = BBB.DTI_STATUS ) dtiStatus , (SELECT CNAME FROM SB_TAX_CODE WHERE GCODE = 'NTS_SEND_STATUS' AND CODE = BBB.NTS_SEND_STATUS ) ntsSendStatus , BBB.NTS_SEND_DATE ntsSendDate , AAA.REGDATE , CASE WHEN ISNULL(AAA.MNG_CUST_ID, '') = '' THEN DDD.BIZ_NAME ELSE CCC.BIZ_NAME END mngCustName , DDD.CAR_NUM mngCarNum , ISNULL(BBB.RESULT, 'N') result , BBB.DETAIL resultDtl FROM T_TAX_INVOICE AAA LEFT OUTER JOIN T_TAX_MANAGE BBB ON AAA.INV_ID = BBB.INV_ID LEFT OUTER JOIN T_CUST_MST CCC ON AAA.MNG_CUST_ID = CCC.CUST_ID LEFT OUTER JOIN T_VEHIC_MST DDD ON AAA.MNG_VEHIC_ID = DDD.VEHIC_ID WHERE AAA.WRITE_DATE BETWEEN #{fromDate} AND #{toDate} AND AAA.USE_YN = 'Y'
AND AAA.SUPPLIER_CUST_ID = #{loginCustId}
AND AAA.BUYER_CUST_ID = #{loginCustId}
AND AAA.LOADSTATUS = #{sLoadStatus}
AND AAA.ISSUE_ID = #{s_issueId}
AND DDD.CAR_NUM LIKE '%'+ #{s_mngCarNum} +'%'
) AA ) A WHERE number BETWEEN #{start} AND #{end} ORDER BY number ASC

SELECT COUNT(*) FROM T_TAX_INVOICE AAA LEFT OUTER JOIN T_TAX_MANAGE BBB ON AAA.INV_ID = BBB.INV_ID LEFT OUTER JOIN T_CUST_MST CCC ON AAA.MNG_CUST_ID = CCC.CUST_ID LEFT OUTER JOIN T_VEHIC_MST DDD ON AAA.MNG_VEHIC_ID = DDD.VEHIC_ID WHERE AAA.WRITE_DATE BETWEEN #{fromDate} AND #{toDate} AND AAA.USE_YN = 'Y'
AND AAA.SUPPLIER_CUST_ID = #{loginCustId}
AND AAA.BUYER_CUST_ID = #{loginCustId}
AND AAA.ISSUE_ID = #{s_issueId}
AND DDD.CAR_NUM LIKE '%'+ #{s_mngCarNum} +'%'

SELECT * FROM (
SELECT TOP 1 CM.CUST_ID supplierCustId, U.DEPT_ID supplierDeptId, CM.BIZ_NUM supplierBizNo, CM.BIZ_NUM_SUB supplierBizSubNo, CM.BIZ_NAME supplierBizName, CM.CEO supplierCeo, U.TELNUM supplierMemTel, CM.BIZ_COND supplierBizType, CM.BIZ_KIND supplierBizClass, CM.BIZ_ADDR supplierAddr, (SELECT DEPT_NAME FROM T_CUST_DEPT WHERE DEPT_ID = #{supplierDeptId}) supplierMemDept, U.EMAIL supplierMemEmail FROM T_CUST_MST CM, T_USER U WHERE CM.CUST_ID = U.CUST_ID AND CM.CUST_ID = #{supplierCustId} AND U.DEPT_ID = #{supplierDeptId}
SELECT TOP 1 B.VEHIC_ID supplierVehicId, A.DRIVER_ID supplierDriverId, B.BIZ_NUM supplierBizNo, B.SUB_BIZ_NUM supplierBizSubNo, B.BIZ_NAME supplierBizName, B.CEO supplierCeo, A.MOBILE supplierMemTel, B.BIZ_COND supplierBizType, B.BIZ_KIND supplierBizClass, B.BIZ_ADDR supplierAddr FROM T_DRIVER A INNER JOIN T_VEHIC_MST B ON A.VEHIC_ID = B.VEHIC_ID WHERE A.DRIVER_ID = #{driverId} AND B.VEHIC_ID = #{vehicId}
) TFR, ( SELECT TOP 1 CM.CUST_ID buyerCustId, U.DEPT_ID buyerDeptId, CM.BIZ_NUM buyerBizNo, CM.BIZ_NUM_SUB buyerBizSubNo, CM.BIZ_NAME buyerBizName, CM.CEO buyerCeo, U.TELNUM buyerMemTel, CM.BIZ_COND buyerBizType, CM.BIZ_KIND buyerBizClass, CM.BIZ_ADDR buyerAddr, (SELECT DEPT_NAME FROM T_CUST_DEPT WHERE DEPT_ID = #{buyerDeptId}) buyerMemDept, U.EMAIL buyerMemEmail FROM T_CUST_MST CM, T_USER U WHERE CM.CUST_ID = U.CUST_ID AND CM.CUST_ID = #{buyerCustId} AND U.DEPT_ID = #{buyerDeptId} ) TTO

