mapper.update:
UPDATE T_CALC SET TAXINV_YN = 'Y', TAXINV_DATE = GETDATE() WHERE ALLOC_ID IN
#{item.allocId, jdbcType=NVARCHAR}
AND ORDER_ID IN
#{item.orderId, jdbcType=NVARCHAR}

update.foreach:
   		UPDATE T_TAX_ITEM
		SET 
			ITEM_DATE = #{item.itemDate}
		  , ITEM_NAME = #{item.itemName}
		  , ITEM_REMARKS = #{item.itemRemarks}
   		WHERE INV_ID = #{invId}
   		AND SEQ = #{item.seq}

mapper.update:
