mapper.insert:
		{call INSERT_TRAN_RECEIPT(
							#{loginCustId}
						  , #{mngDeptId}
						  , #{custId}
						  , #{deptId}
						  , #{vehicId}
						  , #{driverId}
						  , #{fromDate}	
						  , #{toDate}
						  , #{sellBuySctn}
  						  , #{bizTypeCd}
  						  , #{orderList}
  						  , #{writeDate}
						  , #{regId}
						  , #{retId, mode=OUT, jdbcType=NVARCHAR, javaType=String}
						  , #{retCode, mode=OUT, jdbcType=NVARCHAR, javaType=String}
						  ,	#{retMsg, mode=OUT, jdbcType=NVARCHAR, javaType=String})
		}

		{call INSERT_TRAN_RECEIPT_FOR_DRIVER(
							#{loginCustId}
						  , #{mngDeptId}
						  , #{custId}
						  , #{deptId}
						  ,	#{vehicId}
						  , #{driverId}
						  , #{fromDate}
						  , #{toDate}
						  , #{sellBuySctn}
  						  , #{bizTypeCd}
						  , #{orderList}
						  , #{writeDate}
						  , #{regId}
						  , #{retId, mode=OUT, jdbcType=NVARCHAR, javaType=String}
						  , #{retCode, mode=OUT, jdbcType=NVARCHAR, javaType=String}
						  ,	#{retMsg, mode=OUT, jdbcType=NVARCHAR, javaType=String})
		}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY AA.sortRegDate DESC) number , AA.* FROM ( SELECT TR.RECEIPT_ID receiptId, TR.REGDATE sortRegDate, CONVERT(CHAR(10), TR.REGDATE, 23) regDate, TR.FR_BIZ_NAME frBizName, TR.TO_BIZ_NAME toBizName, FORMAT(ISNULL(TR.CHARGE, 0), '##,##0') charge, FORMAT(ISNULL(TR.VAT, 0), '##,##0') vat, FORMAT(ISNULL(TR.AMT, 0), '##,##0') amt, (SELECT COUNT(1) FROM T_TRAN_RECEIPT_DTL WHERE RECEIPT_ID = TR.RECEIPT_ID ) totalCnt, (SELECT CNAME FROM T_CODE WHERE GCODE = 'BANK_CD' AND CODE = ISNULL(TCM.BANK_CODE, D.BANK_CODE)) bankName, ISNULL(TCM.BANK_CNNM, D.BANK_CNNM) bankCnnm, ISNULL(TCM.BANK_ACCOUNT, D.BANK_ACCOUNT) bankAccount, TR.REGID regId FROM T_TRAN_RECEIPT TR LEFT OUTER JOIN T_CUST_MST TCM ON TR.FR_CUST_ID = TCM.CUST_ID LEFT OUTER JOIN T_DRIVER D ON TR.FR_DRIVER_ID = D.DRIVER_ID AND TR.FR_VEHIC_ID = D.VEHIC_ID WHERE TR.USE_YN = 'Y' AND ( ( TR.FR_CUST_ID = #{loginCustId}
AND TR.FR_DEPT_ID = #{mngDeptId}
) OR ( TR.TO_CUST_ID = #{loginCustId}
AND TR.TO_DEPT_ID = #{mngDeptId}
) )
AND CONVERT(CHAR(10), TR.REGDATE, 23) BETWEEN #{fromDate} AND #{toDate}
AND TR.FR_CUST_ID = #{frBizName}
AND TR.TO_CUST_ID = #{toBizName}
) AA ORDER BY number ASC

mapper.update:
   		{call DELETE_TRAN_RECEIPT (
							#{receiptId}
						  , #{regId}
						  , #{retCode, mode=OUT, jdbcType=NVARCHAR, javaType=String}
						  ,	#{retMsg, mode=OUT, jdbcType=NVARCHAR, javaType=String})
   		}
