mapper.insert:
INSERT INTO T_BOARD_FILE ( BOARD_SEQ , FILE_NAME , FILE_REAL_NAME , FILE_PATH , FILE_SIZE , MIME_TYPE , REGID , F_NO ) SELECT A.* FROM (
SELECT #{i.boardSeq} boardSeq , #{i.fileName} fileName , #{i.fileRealName} fileRealName , #{i.filePath} filePath , #{i.fileSize, jdbcType=BIGINT} fileSize , #{i.mimeType} mineType , #{i.regid} regid , #{i.fNo} fNo
) A

mapper.delete:
DELETE FROM T_BOARD_FILE
WHERE 1=1

  	AND BOARD_SEQ IN

  		#{item, jdbcType=NVARCHAR}



	AND FILE_SEQ = #{fileSeq}

