mapper.select:
SELECT AA.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY AAA.carNum ASC, AAA.driverName ASC, AAA.deptName ASC, AAA.mobile ASC, AAA.regdate ASC) rNum , AAA.* FROM ( SELECT A.VEHIC_ID vehicId , B.DRIVER_ID driverId , C.CUST_ID custId , C.DEPT_ID deptId , C.CAR_SCTN_CODE carSctnCode , (SELECT CNAME FROM T_CODE WHERE CODE = C.CAR_SCTN_CODE AND GCODE = 'CARGO_TRAN_CAR_SCTN_CD') carSctnName , C.MEMO memo , A.CAR_NUM carNum , (SELECT CNAME FROM T_CODE WHERE CODE = A.CAR_TYPE_CODE AND GCODE = 'CAR_TYPE_CD') carTypeName , (SELECT CNAME FROM T_CODE WHERE CODE = A.CAR_TON_CODE AND GCODE = 'CAR_TON_CD') carTonName , A.CAR_TYPE_CODE carTypeCode , A.CAR_TON_CODE carTonCode , A.BIZ_NAME bizName , A.BIZ_NUM bizNum , A.SUB_BIZ_NUM subBizNum , A.CEO ceo , A.POSTAL_POST postalPost , A.POSTAL_ADDR postalAddr , A.POSTAL_ADDR_DETAIL postalAddrDetail , A.BIZ_POST bizPost , A.BIZ_ADDR bizAddr , A.BIZ_ADDR_DETAIL bizAddrDetail , A.BIZ_COND bizCond , A.BIZ_KIND bizKind , ISNULL(A.DANGER_GOODS_YN,'N') dangerGoodsYn , ISNULL(A.CHEMICALS_YN,'N') chemicalsYn , ISNULL(A.FOREIGN_LICENSE_YN,'N') foreignLicenseYn , ISNULL(A.FORKLIFT_YN,'N') forkliftYn , B.DRIVER_NAME driverName , B.MOBILE mobile , B.TELNUM telNum , B.CID cid , B.PUSH_YN pushYn , B.TALK_YN talkYn , (SELECT CNAME FROM T_CODE WHERE CODE = B.BANK_CODE) bankName , B.BANK_CODE bankCode , B.BANK_CNNM bankCnnm , B.BANK_ACCOUNT bankAccount , convert(varchar, B.REGDATE, 120) regdate , convert(varchar, C.DISUSE_DATE, 120) disuseDate , C.USE_YN useYn , B.DEVICE_MODEL deviceModel , B.DEVICE_OS deviceOs , B.APP_VERSION appVersion , C.CUST_VEHIC_SEQ custVehicSeq , C.MANAGER userId , C.CAR_MNG_CODE carMngCode , (SELECT CNAME FROM T_CODE WHERE CODE = C.CAR_MNG_CODE AND GCODE = 'CAR_MNG_CD' ) carMngName , C.CAR_MNG_MEMO carMngMemo , (SELECT DEPT_NAME FROM T_CUST_DEPT WHERE DEPT_ID = C.DEPT_ID) deptName , A.CARGO_BOX cargoBox , A.MIX_PREFER_YN mixPreferYn , C.PAY_TYPE payType , C.PAY_MEMO payMemo , B.DRIVER_EMAIL driverEmail , B.TAXJOIN_DATE taxjoinDate , A.CAR_MODIFY_CODE carModifyCode , C.CAR_CONTRACT_CODE carContractCode FROM T_VEHIC_MST A INNER JOIN T_DRIVER B ON A.DRIVER_ID = B.DRIVER_ID
LEFT OUTER JOIN
INNER JOIN
T_CUST_VEHIC C ON A.DRIVER_ID = C.DRIVER_ID AND A.VEHIC_ID = C.VEHIC_ID AND C.CUST_ID = #{loginCustId}
AND C.DEPT_ID = #{deptId}
WHERE 1=1
AND C.USE_YN = #{useYn}
AND ${select} LIKE '%'+ #{value} +'%'
AND A.CAR_NUM LIKE '%'+ #{carNum} +'%'
AND A.DANGER_GOODS_YN = #{dangerGoodsUseYn}
AND A.CHEMICALS_YN = #{chemicalsUseYn}
AND A.FOREIGN_LICENSE_YN = #{foreignLicenseUseYn}
AND A.FORKLIFT_YN = #{forkliftUseYn}
AND C.CAR_MNG_CODE = #{carMngCode}
AND A.BIZ_NAME LIKE '%' + #{bizName} + '%'
AND C.CAR_SCTN_CODE = #{carSctnCode}
AND C.CAR_CONTRACT_CODE = #{carContractCode}
AND A.CAR_TYPE_CODE = #{carTypeCode}
AND A.CAR_TON_CODE = #{carTonCode}
AND ISNULL(C.PAY_TYPE, 'N') = #{payType}
AND CASE WHEN ISNULL(B.TAXJOIN_DATE, 'N') = 'N' THEN 'N' ELSE 'Y' END = #{taxjoinYn}
AND CASE WHEN ISNULL(B.DRIVER_EMAIL, '') = '' THEN 'N' ELSE 'Y' END = #{emailYn}
AND CASE WHEN ISNULL(B.BANK_ACCOUNT, '') = '' THEN 'N' ELSE 'Y' END = #{accountYn}
) AAA ) AA WHERE rNum BETWEEN #{start} AND #{end} ORDER BY rNum ASC

SELECT 
	COUNT(1) retCnt
FROM T_VEHIC_MST A
   	 INNER JOIN
   	 T_DRIVER B ON A.DRIVER_ID = B.DRIVER_ID

   		LEFT OUTER JOIN


   		INNER JOIN

   	 T_CUST_VEHIC C ON A.DRIVER_ID = C.DRIVER_ID AND A.VEHIC_ID = C.VEHIC_ID AND C.CUST_ID = #{loginCustId}

   		AND C.DEPT_ID = #{deptId}

WHERE 1=1

	AND A.USE_YN = #{useYn}


	AND ${select} LIKE '%'+ #{value} +'%'


	AND A.CAR_NUM LIKE '%'+ #{carNum} +'%'


	AND A.DANGER_GOODS_YN = #{dangerGoodsUseYn}


	AND A.CHEMICALS_YN = #{chemicalsUseYn}


	AND A.FOREIGN_LICENSE_YN = #{foreignLicenseUseYn}


	AND A.FORKLIFT_YN = #{forkliftUseYn}


	AND C.CAR_MNG_CODE = #{carMngCode}


	AND C.CAR_SCTN_CODE = #{carSctnCode}


	AND C.CAR_CONTRACT_CODE = #{carContractCode}


	AND A.CAR_TYPE_CODE = #{carTypeCode}


	AND A.CAR_TON_CODE = #{carTonCode}


	AND ISNULL(C.PAY_TYPE, 'N') = #{payType}


	AND CASE WHEN ISNULL(B.TAXJOIN_DATE, 'N') = 'N'  THEN 'N'  ELSE 'Y' END = #{taxjoinYn}


	AND CASE WHEN ISNULL(B.DRIVER_EMAIL, '') = ''  THEN 'N'  ELSE 'Y' END = #{emailYn}


	AND CASE WHEN ISNULL(B.BANK_ACCOUNT, '') = ''  THEN 'N'  ELSE 'Y' END = #{accountYn}

SELECT AA.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY AAA.carNum ASC, AAA.driverName ASC, AAA.mobile ASC) rNum , AAA.* FROM ( SELECT DISTINCT A.VEHIC_ID vehicId , B.DRIVER_ID driverId , A.CAR_NUM carNum , B.DRIVER_NAME driverName , B.MOBILE mobile FROM T_VEHIC_MST A INNER JOIN T_DRIVER B ON A.DRIVER_ID = B.DRIVER_ID INNER JOIN T_CUST_VEHIC C ON A.DRIVER_ID = C.DRIVER_ID AND A.VEHIC_ID = C.VEHIC_ID AND C.CUST_ID = #{loginCustId}
AND C.DEPT_ID = #{deptId}
WHERE C.USE_YN = 'Y'
AND A.CAR_NUM LIKE '%'+ #{carNum} +'%'
AND C.CAR_CONTRACT_CODE = #{carContractCode}
) AAA ) AA WHERE rNum BETWEEN #{start} AND #{end} ORDER BY rNum ASC

SELECT COUNT(1) retCnt FROM ( SELECT DISTINCT A.VEHIC_ID vehicId , B.DRIVER_ID driverId , A.CAR_NUM carNum , B.DRIVER_NAME driverName , B.MOBILE mobile FROM T_VEHIC_MST A INNER JOIN T_DRIVER B ON A.DRIVER_ID = B.DRIVER_ID INNER JOIN T_CUST_VEHIC C ON A.DRIVER_ID = C.DRIVER_ID AND A.VEHIC_ID = C.VEHIC_ID AND C.CUST_ID = #{loginCustId}
AND C.DEPT_ID = #{deptId}
WHERE C.USE_YN = 'Y'
AND A.CAR_NUM LIKE '%'+ #{carNum} +'%'
AND C.CAR_CONTRACT_CODE = #{carContractCode}
) AAA

